sys_variable_value.value:
(function execute(inputs, outputs) {

    // inputs = inputs.replace( /<br\/?>/g, '\n' );        // Replace break tags with new lines.
    // inputs = inputs.replace( /<\/?[^>]+.*?>/g, '' );    // Remove HTML tags
    inputs = inputs.split( '\n' );                      // Split on new lines

    outputs = {};

    inputs
        .filter( function( row ){

            return row.test( /^\w+:\w+$/g );
            
        } )
        .map( function( pair ){

            pair = pair.split( ':' );
            var key = pair[0];
            var value = pair[1];
            
            output[ key ] = value;
        
        } );

    return outputs;


})(inputs, outputs);

